//FUNCION CONSTRUCTORA

function Song(title,artist,year,gender,album,poster)
{
	o = this;
	o.title = title;
	o.artist = artist;
	o.year = year;
	o.gender = gender;
	o.album = album;
	o.poster = poster;
	o.getInfo = function ()
	{
		return '<article> <h2>' + o.title + ' ' + o.artist + '</h2> <img src="' + o.poster + '"/><p>' + o.gender+ '</p><p>' + o.album+ '</p><p>' + o.year+ '</p></article>'	
	}
}


