//CLASE 
 
class SongClass 
{
	constructor(title,artist,year,gender,album,poster)
	{
		this.title = title;
		this.artist = artist;
		this.year = year;
		this.gender = gender;
		this.album = album;
		this.poster = poster;
	}
	getInfo()
	{
		return '<article> <h2>' + this.title + ' ' + this.artist + '</h2> <img src="' + this.poster + '"/><p>' + this.gender + '</p><p>' + this.album + '</p><p>' + this.year + '</p></article>'	
	}
}


